<?php

namespace App\Models;

use Illuminate\Database\Eloquent\Model;
use Illuminate\Database\Eloquent\Relations\BelongsTo;

class TemplateField extends Model
{
    protected $fillable = [
        'template_id',
        'field_name',
        'field_type',
        'placeholder',
        'position_data',
        'is_required',
        'default_value',
    ];

    protected $casts = [
        'position_data' => 'array',
        'is_required' => 'boolean',
    ];

    /**
     * Field types
     */
    const TYPE_TEXT = 'text';
    const TYPE_DATE = 'date';
    const TYPE_AUTO_ID = 'auto_id';
    const TYPE_NUMBER = 'number';

    /**
     * Get the template that owns this field
     */
    public function template(): BelongsTo
    {
        return $this->belongsTo(Template::class);
    }

    /**
     * Check if field is auto-generated
     */
    public function isAutoGenerated(): bool
    {
        return $this->field_type === self::TYPE_AUTO_ID;
    }

    /**
     * Generate value for auto fields
     */
    public function generateAutoValue(?int $index = null): string
    {
        if ($this->field_type === self::TYPE_AUTO_ID) {
            $prefix = $this->default_value ?? 'CERT';
            $number = str_pad($index ?? rand(1000, 9999), 6, '0', STR_PAD_LEFT);
            return $prefix . '-' . $number;
        }

        return '';
    }
}
